$(document).ready(function() {
    const $accordion = $(".accordion");
    // Initially expand the first accordion item
    $accordion.find(".accordion-item:first-child").addClass("is-active").find(".accordion-panel").slideDown();

    $accordion.on("click", ".accordion-item", function(event) {
        const $item = $(this);
        const isActive = $item.hasClass("is-active");
        // Collapse all items except the clicked one
        $accordion.find(".accordion-item.is-active").not($item).each(function() {
            const $activeItem = $(this);
            $activeItem.removeClass("is-active");
            $activeItem.find(".accordion-panel").slideUp();
        });
        // Toggle the clicked item
        if (!isActive) {
            $item.addClass("is-active");
            $item.find(".accordion-panel").slideDown();
        } else {
            $item.removeClass("is-active");
            $item.find(".accordion-panel").slideUp();
        }
    });
});
