// Your updated JavaScript code will go here
    const locales = {
      en: "GB",
      hi: "IN",
      pt: "BR",
      es: "ES",
      fil: "PH",
      ar: "SA",
      bn: "BD",
      id: "ID",
      th: "TH",
      vi: "VN"
    };

    const localeUrls = {
      en: "/index.html",
      pt: "/pt/",
      es: "/es/",
      fil: "/fil/",
      ar: "/ar/",
      bn: "/bn/",
      id: "/id/",
      th: "/th/",
      vi: "/vi/"
    };

    function getFlagSrc(countryCode) {
      return /^[A-Z]{2}$/.test(countryCode) ? `https://flagsapi.com/${countryCode}/shiny/64.png` : "";
    }

    const dropdownBtn = document.getElementById("dropdown-btn");
    const dropdownContent = document.getElementById("dropdown-content");

    function setSelectedLocale(language) {
      const region = locales[language];
      const langName = new Intl.DisplayNames([language], { type: "language" }).of(language);

      dropdownContent.innerHTML = "";

      const otherLanguages = Object.keys(locales).filter((lang) => lang !== language);
      otherLanguages.forEach((otherLanguage) => {
        const otherRegion = locales[otherLanguage];
        const otherLangName = new Intl.DisplayNames([otherLanguage], { type: "language" }).of(otherLanguage);

        const listEl = document.createElement("li");
        listEl.innerHTML = `${otherLangName} <img alt="flag" src="${getFlagSrc(otherRegion)}" />`;
        listEl.value = otherLanguage;
        listEl.addEventListener("click", function () {
          setSelectedLocale(otherLanguage);
        });
        dropdownContent.appendChild(listEl);
      });

      dropdownBtn.innerHTML = `<img alt="flag" src="${getFlagSrc(region)}" /> ${langName} <span class="arrow-up"></span>`;

      // Load the corresponding content into the body
      if (localeUrls[language]) {
        $("body").load(localeUrls[language] + " body", function() {
          // Re-initialize the dropdown elements after loading new content
          $("#dropdown-btn").html(dropdownBtn.innerHTML);
          $("#dropdown-content").html(dropdownContent.innerHTML);
          $("#dropdown-content li").each(function() {
            $(this).on("click", function() {
              setSelectedLocale(this.value);
            });
          });
        });
      }
    }

    // Get the current language from the HTML element
    const currentLang = document.documentElement.lang || "en";
    setSelectedLocale(currentLang);
