const locales = {
  en: "GB",
  hi: "IN",
  pt: "BR",
  es: "ES",
  fil: "PH",
  ar: "SA",
  bn: "BD",
  id: "ID",
  th: "TH",
  vi: "VN"
};

const urls = {
  en: "/index.html",
  pt: "/pt/",
  es: "/es/",
  fil: "/fil/",
  ar: "/ar/",
  bn: "/bn/",
  id: "/id/",
  th: "/th/",
  vi: "/vi/"
};

function getFlagSrc(countryCode) {
  return /^[A-Z]{2}$/.test(countryCode) ? `https://flagsapi.com/${countryCode}/shiny/64.png` : "";
}

const dropdownBtn = document.getElementById("dropdown-btn");
const dropdownContent = document.getElementById("dropdown-content");

function setSelectedLocale(locale) {
  const region = locales[locale];
  const langName = new Intl.DisplayNames([locale], { type: "language" }).of(locale);

  dropdownContent.innerHTML = "";

  const otherLocales = Object.keys(locales).filter((loc) => loc !== locale);
  otherLocales.forEach((otherLocale) => {
    const otherRegion = locales[otherLocale];
    const otherLangName = new Intl.DisplayNames([otherLocale], { type: "language" }).of(otherLocale);

    const listEl = document.createElement("li");
    listEl.innerHTML = `${otherLangName} <img alt="flag" src="${getFlagSrc(otherRegion)}" />`;
    listEl.value = otherLocale;
    listEl.addEventListener("click", function () {
      window.location.href = urls[otherLocale];
    });
    dropdownContent.appendChild(listEl);
  });

  dropdownBtn.innerHTML = `<img alt="flag" src="${getFlagSrc(region)}" /> ${langName} <span class="arrow-up"></span>`;
}

// Detect the language from the <html lang="en"> attribute
const htmlLang = document.documentElement.lang || "en";
setSelectedLocale(htmlLang);

if (!locales[htmlLang]) {
  setSelectedLocale("en");
}
