const scriptUrls = [
  '/path/to/your/script1.js',
  '/path/to/your/script2.js'
  // Add more script URLs as needed
];

function loadScripts() {
  const version = Date.now(); // Generate a unique version identifier (timestamp)
  
  scriptUrls.forEach(url => {
    const script = document.createElement('script');
    script.src = `${url}?v=${version}`; // Append version as query parameter
    script.async = false; // Load scripts synchronously for simplicity
    
    document.head.appendChild(script);
  });
}

// Call loadScripts() to load updated versions of scripts
loadScripts();
